echo $var1; // Exibe: 1.5
echo $var2; // Exibe: 5
echo $var3; // Exibe: 3.5, resultado de 5 - 1.5
echo $var4; // Exibe: 20, resultado de 4 vezes 5
echo $var5; // Exibe: 2.5


